node.code:
normal $(Nf)= faceforward( normalize(N), I );
vector $(V)= -normalize(I);
color $(Ia)= ambient() + $(ambientColor);
color $(Id)= $(diffuseCoeff)* diffuse($(Nf));

color $(Itr)= 0;
if ($(translucence)!= 0) {
 illuminance(P, $(Nf), PI)
 $(Itr)+= Cl;
 $(Itr)*= $(translucence);
}

color $(outColor)= $(SurfaceColor) * ($(Ia)+ $(Id)+ $(Itr)+ $(incandescence));
